        //                 item.name.toLowerCase().includes(cloneSearchTerm)
        //                 ||
        //                 item.ordered_item.toLowerCase().includes(cloneSearchTerm)
        //                 ||
        //                 item.address.toLowerCase().includes(cloneSearchTerm)
